    // 根据传递进来的每道题的分数 计算每种体质的分数，返回一个list列表，列表中每个值 代表一种体质的分数和
    public static List<Float> getGraphicalValues(int[] rate) {
        // 转化分数=[（原始分－条目数）／（条目数×4）]×100
        List<Float> values = new ArrayList<Float>();
        {// 循环平和质
            int len = Tzps.PH_INDEX.length;
            int value = rate[Tzps.PH_INDEX[0]] + (5 - rate[Tzps.PH_INDEX[1]])
                    + (5 - rate[Tzps.PH_INDEX[2]]) + (5 - rate[Tzps.PH_INDEX[3]])
                    + (5 - rate[Tzps.PH_INDEX[4]]);
            Float val = ((float) (value - len) / len / 4) * 100;
            values.add(val);
        }
        int[][] questionIndexs = new int[][] { Tzps.QX_INDEX, Tzps.YAX_INDEX, Tzps.YIX_INDEX,
                Tzps.TS_INDEX, Tzps.SR_INDEX, Tzps.XY_INDEX, Tzps.QY_INDEX, Tzps.TL_INDEX };
        for (int i = 0; i < questionIndexs.length; i++) {// 循环判断每个题的个数
            int value = 0;
            int len = questionIndexs[i].length;
            for (int j = 0; j < len; j++) {
                value += rate[questionIndexs[i][j]];
            }
            Float val = ((float) (value - len) / len / 4) * 100;
            values.add(val);
        }
        return values;
    }
